import React, { useState } from "react";
import { Navbar } from "./components";
import MainSection from "./container/MainSection/MainSection";


import "./index.scss";
import "./App.scss";

const App = () => {

  const [bigImageClicked, setBigImageClicked] = useState(false);

  return (
    <div className={`app__bg ${bigImageClicked ? "app__bg-lightbox" : "" }`} onClick={ () => {
      setBigImageClicked(true);
    }}>
      <Navbar />
      <MainSection />
    </div>
  )
};

export default App;
