import React, { useState } from "react";
import { Navbar } from "./components";
import MainSection from "./container/MainSection/MainSection";


import "./index.scss";
import "./App.scss";

const App = () => {

    // logic for quantity buttons
    const [quantity, setQuantity] = useState(0);

    const addOne = () => {
        localStorage.setItem("quantity", quantity + 1);
        setQuantity( quantity + 1);
    }

    const minusOne = () => {
        localStorage.setItem("quantity", quantity - 1);
        setQuantity(quantity - 1);

        if(quantity === 0){
            localStorage.setItem("quantity", quantity);
            setQuantity(quantity);
        }
    }

    const itemsInCart = localStorage.getItem("cartNumber")

    // logic for shopping cart
    const [cartItems, setCartItems] = useState(itemsInCart);

    const cartNumber = () => {
        localStorage.setItem("cartNumber", cartItems + 1);
        setCartItems(cartItems + 1);
    }



  return (
    <div className="app__bg">
      <Navbar 
        cartItems={cartItems}

      />
      <MainSection 
        add={addOne}
        minus={minusOne}
        quantity={quantity}
        cartNumber={cartNumber}
      />
    </div>
  )
};

export default App;
