import React from "react";
import images from "../../constants/images";

import "./MainSection.scss";

const MainSection = () => {

    const imageGallery = [images.product01, images.product02, images.product03, images.product04];

  return (
    <div className="app__mainsection-wrap flex__center">
        <div className="app__mainsection-gallery">
            <img src={images.product01Big} alt="big IMG" />

            <div className="app__mainsection-gallery_thumbnails">
                {imageGallery.map( (image, index) => (
                    <img src={image} alt="thumbnail" key={image + index} />
                ))}
            </div>
        </div>

        <div className="app__mainsection-content">
            <div className="app__mainsection-content_text">
                <h3 className="headtext__kumbhsans" style={{color: "var(--color-orange)"}}>Sneaker Company</h3>
                <h1 className="headtext__kumbhsans">Fall Limited Edition Sneakers</h1>
                <p className="p__kumbhsans">
                These low-profile sneakers are your perfect casual wear companion.
                Featuring a durable rubber outer sole, they'll withstand everything the weather can offer.
                </p>
            </div>

            <div className="app__mainsection-content_price">
                <div className="app__mainsection-content_price_numbers">

                </div>
                <h2 className="headtext__kumbhsans">$125.00</h2>
                <p className="p__kumbhsans">$250.00</p>
            </div>
        </div>
    </div>
  )
}

export default MainSection;
